import React,{Component} from "react"
import pic_1 from "../../assets/img/t3.png"
import Button from '@material-ui/core/Button';
class QRComp extends Component{
  render(){
    return(
      <div style = {box_1}>
        <div style = {box_left}>
          <img src={pic_1} width="190" alt="Card cap 01" className=""style ={pic} />
        </div>

        <div style = {box_center}>
          <p>Name QR :Q1</p>
          <p>URL :https://www.youtube.com/watch?v=t0a7b_QHA24</p>
          <p>Created at :1/1/2019</p>
        </div>

        <div style = {box_right}>
          <p>จำนวนคนสแกน</p>

        </div>

        <div style = {box_rightest}>
          <Button variant="outlined">
            Default
          </Button>
          <Button variant="outlined" >
            Default
          </Button>
          <Button variant="outlined" >
            Default
          </Button>

        </div>

      </div>
    )
  }

}

const containner = {
  height:"100vh",
};

const pic ={
border: '1px solid #000000'
}

const box_1 ={
  // display:'flex',
  width:"100%",
  background:'white',
  margin:5,
  display:'flex',
  flexDirection:'row'
}

const box_left = {

}

const box_center = {
  width: '50%'
}

const box_right = {
  width: '20%',

}

const box_rightest = {
  display: 'flex',
  flexDirection:'column',
  backgroundColor:'skyblue',
  width:'15%',
    alignItems: 'center',
    justifyContent :'center'

}


export default QRComp;
