import React,{Component} from "react"
import MinimalStatisticsBG from "../../components/cards/minimalStatisticsBGCard";
import * as Icon from "react-feather";
import QRStatComp_1 from "./qrstatcomp_1.js"
class QRStatis extends Component{

  constructor(props){
    super(props);
    this.state = {g1: false,g2:false,g3:false,g4:false,g5:false};
  }

  click_1 = () =>{
    this.setState(state => ({
      g1 : !state.g1
    }))
  }

  click_2 = () => {
    this.setState(state => ({
      g2:!state.g2
      }
    )
  )}

  click_3 = () => {
    this.setState(state => ({g3:!state.g3}))
  }

  click_4 = () => {
    this.setState(state => ({g4:!state.g4}))
  }

  render(){
    return(
      <div>

      <div style = {containner} >
          <div style ={{display:'flex',width:"18%",flexDirection:'column' , marginLeft:'auto',marginRight:'auto'}}
            onClick = {this.click_1}
          >
            <MinimalStatisticsBG
               cardBgColor="gradient-blackberry"
               statistics="278"
               text="Total Scan"
               iconSide="right"
            >
               <Icon.Briefcase size={56} strokeWidth="1.3" color="#fff" />
            </MinimalStatisticsBG>
          </div>


          <div style ={{display:'flex',width:"18%",flexDirection:'column' , marginLeft:'auto',marginRight:'auto'}}
          onClick = {this.click_2}
          >
            <MinimalStatisticsBG
               cardBgColor="gradient-blackberry"
               statistics="100"
               text="Unique Users"
               iconSide="right"
            >
               <Icon.Briefcase size={56} strokeWidth="1.3" color="#fff" />
            </MinimalStatisticsBG>
            </div>

            <div style ={{display:'flex',width:"18%",flexDirection:'column' , marginLeft:'auto',marginRight:'auto'}}>
              <MinimalStatisticsBG
                 cardBgColor="gradient-blackberry"
                 statistics="100%"
                 text="Location"
                 iconSide="right"
              >
                 <Icon.Briefcase size={56} strokeWidth="1.3" color="#fff" />
              </MinimalStatisticsBG>
              </div>

              <div style ={{display:'flex',width:"18%",flexDirection:'column' , marginLeft:'auto',marginRight:'auto'}}>
                <MinimalStatisticsBG
                   cardBgColor="gradient-blackberry"
                   statistics="100%"
                   text="Platform"
                   iconSide="right"
                >
                   <Icon.Briefcase size={56} strokeWidth="1.3" color="#fff" />
                </MinimalStatisticsBG>
                </div>

                <div style ={{display:'flex',width:"18%",flexDirection:'column' , marginLeft:'auto',marginRight:'auto'}}>
                  <MinimalStatisticsBG
                     cardBgColor="gradient-blackberry"
                     statistics="Pawut"
                     text="Who Scan"
                     iconSide="right"
                  >
                     <Icon.Briefcase size={56} strokeWidth="1.3" color="#fff" />
                  </MinimalStatisticsBG>
                  </div>


      </div>

      <div>
        {this.state.g1 && <QRStatComp_1 />}
        {this.state.g2 && <QRStatComp_1 />}
      </div>

      </div>

    )
  }
}

const containner ={
  display:'flex',
  flexDirection:'row',

}

const box = {
  backgroundColor :'skyblue',
  width:'18%',
  height:'11%',
  marginLeft:'auto',
  marginRight:'auto'
}

export default QRStatis;
