import React,{Component} from "react"
import MinimalStatisticsBG from "../../components/cards/minimalStatisticsBGCard";
import * as Icon from "react-feather";
import QRStatComp_1 from "./qrstatcomp_1.js"
import QRStatComp_2 from "./qrstatcomp_2.js"
import QRStatComp_3 from "./qrstatcomp_3.js"
import QRStatComp_4 from "./qrstatcomp_4.js"
import QRStatComp_5 from "./qrstatcomp_5.js"
import Table_stat from "./table_stat.js"
class QRStatis extends Component{

  constructor(props){
    super(props);
    this.state = {g1: false,g2:false,g3:false,g4:false,g5:false,labels:{},datasets:[]};

  }

  f_1 = () =>{
    fetch('https://yourqr.today/api/v1/qr.checkqr/2455')
      .then((response) => response.json())
      .then(data => {
        console.log(data.c_data)
        this.setState(state => ({labels: data.c_data[0].c }))
        })


  }

  click_1 = () =>{
    this.setState(state => ({
      g1 : !state.g1,
      g2 : false,
      g3 : false,
      g4 :false,
      g5:false
    }))

    this.f_1()

    }

  click_2 = () => {
    this.setState(state => ({
      g2:!state.g2,
      g1 : false,
      g3 : false,
      g4:false,
      g5:false
      }
    )
  )}

  click_3 = () => {
    this.setState(state => ({g3:!state.g3,
    g1 : false,
    g2 : false,
    g4: false,
    g5: false
    }))
  }

  click_4 = () => {
    this.setState(state => ({g4:!state.g4,
    g1 : false,
    g2 : false,
    g3: false,
    g5: false


    }))
  }

  click_5 = () => {
    this.setState(state => ({g5:!state.g5}))
  }

  render(){
    return(
      <div>

      <div style = {containner} >
          <div style ={{display:'flex',width:"18%",flexDirection:'column' , marginLeft:'auto',marginRight:'auto'}}
            onClick = {this.click_1}
          >
            <MinimalStatisticsBG
               cardBgColor="gradient-blackberry"
               statistics="278"
               text="Total Scan"
               iconSide="right"
            >
               <Icon.Briefcase size={56} strokeWidth="1.3" color="#fff" />
            </MinimalStatisticsBG>
          </div>


          <div style ={{display:'flex',width:"18%",flexDirection:'column' , marginLeft:'auto',marginRight:'auto'}}
          onClick = {this.click_2}
          >
            <MinimalStatisticsBG
               cardBgColor="gradient-blackberry"
               statistics="100"
               text="Unique Users"
               iconSide="right"
            >
               <Icon.Briefcase size={56} strokeWidth="1.3" color="#fff" />
            </MinimalStatisticsBG>
            </div>

            <div style ={{display:'flex',width:"18%",flexDirection:'column' , marginLeft:'auto',marginRight:'auto'}}
            onClick = {this.click_3}
            >
              <MinimalStatisticsBG
                 cardBgColor="gradient-blackberry"
                 statistics="100%"
                 text="Location"
                 iconSide="right"
              >
                 <Icon.Briefcase size={56} strokeWidth="1.3" color="#fff" />
              </MinimalStatisticsBG>
              </div>

              <div style ={{display:'flex',width:"18%",flexDirection:'column' , marginLeft:'auto',marginRight:'auto'}}
              onClick = {this.click_4}
              >
                <MinimalStatisticsBG
                   cardBgColor="gradient-blackberry"
                   statistics="100%"
                   text="Platform"
                   iconSide="right"
                >
                   <Icon.Briefcase size={56} strokeWidth="1.3" color="#fff" />
                </MinimalStatisticsBG>
                </div>

                <div style ={{display:'flex',width:"18%",flexDirection:'column' , marginLeft:'auto',marginRight:'auto'}}
                onClick = {this.click_5}
                >
                  <MinimalStatisticsBG
                     cardBgColor="gradient-blackberry"
                     statistics="Pawut"
                     text="Who Scan"
                     iconSide="right"
                  >
                     <Icon.Briefcase size={56} strokeWidth="1.3" color="#fff" />
                  </MinimalStatisticsBG>
                  </div>


      </div>

      <div>
        {this.state.g1 && <QRStatComp_1 />}
        {this.state.g2 && <QRStatComp_2 />}
        {this.state.g3 && <QRStatComp_3 />}
        {this.state.g4 && <QRStatComp_4 />}
      </div>

      <div>
        <Table_stat />
      </div>

      </div>

    )
  }
}

const containner ={
  display:'flex',
  flexDirection:'row',

}

const box = {
  backgroundColor :'skyblue',
  width:'18%',
  height:'11%',
  marginLeft:'auto',
  marginRight:'auto'
}

export default QRStatis;
